void process_fucking_light_bar()  //简单处理因为光斑造成的逻辑上的错误点
{
  if (first_R_spot[1] < 80 && left_edge[first_R_spot[1]] != -1)
  {
    if (right_edge[first_R_spot[1]] < left_edge[first_R_spot[1]])
    {
      for (int16 extract_row = first_R_spot[1]; extract_row > 0; extract_row--)
      {
        if ((right_edge[extract_row] < left_edge[extract_row]) && right_edge[extract_row] != -1)
        {
          right_edge[extract_row] = -1;
        }
      }
    }
  }
  if (first_L_spot[1] < 80 && left_edge[first_L_spot[1]] != -1)
  {
    if (left_edge[first_L_spot[1]] < right_edge[first_L_spot[1]])
    {
      for (int16 extract_row = first_L_spot[1]; extract_row > 0; extract_row--)
      {
        if ((right_edge[extract_row] < left_edge[extract_row]) && right_edge[extract_row] != -1)
        {
          left_edge[extract_row] = -1;
        }
      }
    }
  }
}

/**********大S远处噪点处理函数***********/
void Big_S_distant_bad_dots_processing()
{
  if (!enable_loop_flag && (!Enable_cross_flag)) //不是十字圆环
  {
    if (L_Init_Continuous_Count > 50 && R_Init_Continuous_Count < 20) //初步判断为右大S
    {
      if (L_right_trend_count > 40 && L_left_trend_count > 2)
      {
        if (left_edge[L_right_inflection_point] > 130)
        {
          int16 S_extract_row = 0;
          for (S_extract_row = L_right_inflection_point - L_left_trend_count; S_extract_row >= 0; S_extract_row--)
          {
            left_edge[S_extract_row] = -1;
            right_edge[S_extract_row] = -1;
          }
          for (S_extract_row = L_right_inflection_point - 5; S_extract_row >= (L_right_inflection_point - L_left_trend_count); S_extract_row--)
          {
            right_edge[S_extract_row] = -1;
          }
        }
      }
    }
    else if (R_Init_Continuous_Count > 50 && L_Init_Continuous_Count < 20) //初步判断为右大S
    {
      if (R_left_trend_count > 40 && R_right_trend_count > 2)
      {
        if (right_edge[R_left_inflection_point] < 30)
        {
          int16 S_extract_row = 0;
          for (S_extract_row = R_left_inflection_point - R_right_trend_count; S_extract_row >= 0; S_extract_row--)
          {
            left_edge[S_extract_row] = -1;
            right_edge[S_extract_row] = -1;
          }
          for (S_extract_row = R_left_inflection_point - 5; S_extract_row >= (R_left_inflection_point - R_right_trend_count); S_extract_row--)
          {
            left_edge[S_extract_row] = -1;
          }
        }
      }
    }
  }

}

/***********噪点滤波函数**********/
void delete_bad_dots()
{
  int16 extract_row = ROW_END;
  for (extract_row = ROW_END - 2; extract_row >= 2; extract_row--)
  {
    if (left_edge[extract_row] != -1)
    {
      if (left_edge[extract_row - 1] == -1 && left_edge[extract_row + 1] == -1)
      {
        left_edge[extract_row] = -1;
      }
      if (right_edge[extract_row - 1] == -1 && right_edge[extract_row + 1] == -1)
      {
        right_edge[extract_row] = -1;
      }
    }
  }
}


/***********十字错误处理函数*************/
void process_cross_error()
{
  int16 cross_row;
  if (slope_cross_L_flag == 1 || slope_cross_R_flag == 2)
  {
    for (cross_row = left_bottom.y + 3; cross_row >= 0; cross_row--)
    {
      if (left_edge[cross_row] >= right_edge[cross_row] && right_edge[cross_row] != -1)
      {
        left_edge[cross_row] = -1;
      }
    }
  }
  else if (slope_cross_R_flag == 1 || slope_cross_R_flag == 2)
  {
    for (cross_row = right_bottom.y + 3; cross_row >= 0; cross_row--)
    {
      if (left_edge[cross_row] >= right_edge[cross_row] && right_edge[cross_row] != -1)
      {
        right_edge[cross_row] = -1;
      }
    }
  }
}

//uint8 podao_flag=0;
void detect_podao()
{
  if(L_Init_Continuous_Count>85&&R_Init_Continuous_Count>85)
  {
    int16 podao_row;
    uint8 podao_count=0;
    uint8 podao_straight_count=0;
    for(podao_row=ROW_END;podao_row>0;podao_row--)
    {
      if(left_edge[podao_row]!=-1&&right_edge[podao_row]!=-1)
      {
        if(podao_row<30)
        {
          if((right_edge[podao_row]-left_edge[podao_row]-straight_road[podao_row])>=5)
          {
             podao_count+=1;
          }   
        }
        else
        {
          if(abs((right_edge[podao_row]-left_edge[podao_row]-straight_road[podao_row]))<=4)
          {
            podao_straight_count+=1;
          }
        }
      }
    }
    if(podao_straight_count>49&&podao_count>25)
    {
      down_flag=1;
    }
  }
}

