  //  lptmr_time_close();
  inv_all_edge();      //整体逆透视函数

  /**********调试**********/
  if (down_flag == 1)
  {
    Bee_flag = 1;
    //        bee_time = 10;
  }
//  else if (R_Big_S_flag == 1)
