/**********起跑线识别函数**********/
void qipaoxian_check(uint8 a)
{
  uint8 qipaoxian_zuo_jizhun = 0;
  uint8 qipaoxian_check_num = 0;
  qipaoxian_zuo_jizhun = left_edge[a] + 1;
  for (uint8 pp = qipaoxian_zuo_jizhun; pp < right_edge[a]; pp++)
  {
    if (img_barrel[a - 3][pp] > img_threshold && img_barrel[a - 3][pp + 1] <= img_threshold)
    {
      qipaoxian_check_num++;
      qipaoxian_zuo_jizhun = pp;
    }
    if (qipaoxian_check_num == 7)
    {
      count_stop += 1;
      break;
    }
  }
}


/***********画弧函数************/
void draw_R_arcs(uint8 x, uint8 y)         //画右环圆弧,输入为人字顶点
{
  uint8 center_x = 0;        //圆心横坐标
  uint8 circle_x = 0;        //算得的横坐标
  uint8 center_y = 0;        //圆心纵坐标
  int8 loop_temp = 0;        //循环变量
  int16 R_square = 0;        //圆半径
  unsigned long x_square = 0;
  int16 sqrt_x = 0;
  uint8 init_L_edge = 0;     //左边沿初始非0横坐标
  uint8 translation_distance = 0;  //平移距离
  if (y > 80)
  {
    y = 32;
  }
  /*****确定最下方左边沿初始非空坐标*****/
  if (left_edge[ROW_END - 1] != -1) //最下方的左边沿非空
  {
    init_L_edge = left_edge[ROW_END - 1];
  }
  else
  {
    init_L_edge = 0;
  }
  R_square = (ROW_END - y) * (ROW_END - y) + (COL_END - x) * (COL_END - x);
  translation_distance = (COL_END - sqrt(R_square) - init_L_edge) / 8;
  memset(left_edge, -1, sizeof(left_edge));
  memset(right_edge, -1, sizeof(right_edge));
  memset(midline, -1, sizeof(midline));
  for (loop_temp = y; loop_temp <= ROW_END; loop_temp++)
  {
    x_square = R_square - (ROW_END - loop_temp) * (ROW_END - loop_temp);
    sqrt_x = sqrt(x_square);
    circle_x = COL_END - sqrt_x;
    if (circle_x >= 0 && circle_x <= COL_END)
    {
      midline[loop_temp] = circle_x - translation_distance;
    }
    else
    {
      break;
    }
  }
}

void draw_L_arcs(uint8 x, uint8 y)         //画左环圆弧,输入为人字顶点
{
  uint8 center_x = 0; //圆心横坐标
  uint8 circle_x = 0;          //算得的横坐标
  uint8 center_y = 0; //圆心纵坐标
  int8 loop_temp = 0; //循环变量
  int16 L_square = 0;        //圆半径
  unsigned long x_square = 0;
  int16 sqrt_x = 0;
  uint8 init_R_edge = 0; //右边沿初始非0横坐标
  uint8 translation_distance = 0;  //平移距离


  /*****确定最下边右边沿初始非空坐标*****/
  if (right_edge[ROW_END - 1] != -1) //最下方的左边沿非空
  {
    init_R_edge = right_edge[ROW_END - 1];
  }
  else
  {
    init_R_edge = COL_END;
  }
  L_square = (ROW_END - y) * (ROW_END - y) + (x) * (x);
  translation_distance = (init_R_edge - sqrt(L_square)) / 8;
  memset(left_edge, -1, sizeof(left_edge));
  memset(right_edge, -1, sizeof(right_edge));
  memset(midline, -1, sizeof(midline));

  for (loop_temp = y; loop_temp <= ROW_END; loop_temp++)
  {
    x_square = L_square - (ROW_END - loop_temp) * (ROW_END - loop_temp);
    sqrt_x = sqrt(x_square);
    circle_x = sqrt_x;
    if (circle_x >= 0 && circle_x <= COL_END)
    {
      midline[loop_temp] = circle_x + translation_distance;
    }
    else
    {
      break;
    }
  }
}

/**************障碍补线函数**************/
void patch_barrier()
{
  if (L_Init_Continuous_Count > 10 && R_Init_Continuous_Count > 10) //左右初始边沿都大于0，排除基本错误
  {
    if (Bar_flag == 1) //如果为左障碍
    {
      int8 bar_loop_temp = 0; //障碍循环变量
      uint8 bar_down[2];       //障碍末端下角点
      uint8 bar_up[2];         //障碍上角点
      double bar_k;           //障碍斜率
      L_down_bar_spot[0] = left_edge[L_down_bar_spot[1] - 1] + 5;
      bar_up[1] = left_bottom.y;
      bar_up[0] = left_bottom.x;

      /****延长障碍左上角点到图像最上方****/

      bar_k = (bar_up[0] - L_down_bar_spot[0]) / (L_down_bar_spot[1] - bar_up[1]);
      for (bar_loop_temp = bar_up[1]; bar_loop_temp >= 1; bar_loop_temp--)
      {
        left_edge[bar_loop_temp] = bar_up[0] + bar_k * (bar_up[1] - bar_loop_temp) + 5;
      }
      /****障碍中包络外移****/
      for (bar_loop_temp = L_down_bar_spot[1]; bar_loop_temp > bar_up[1]; bar_loop_temp--)
      {
        left_edge[bar_loop_temp] += 5;
      }
      /****连接障碍左下角点到左边沿最末端位置*****/
      for (bar_loop_temp = ROW_END; bar_loop_temp > 0; bar_loop_temp--)
      {
        if (left_edge[bar_loop_temp] != -1) //如果非空
        {
          bar_down[0] = left_edge[bar_loop_temp];
          bar_down[1] = bar_loop_temp;
          break;
        }
      }
      if (L_down_bar_spot[1] < 40) //如果尚未靠近障碍
      {
        /******连线******/
        bar_k = (L_down_bar_spot[0] - bar_down[0]) / (double)(bar_down[1] - L_down_bar_spot[1]); //计算斜率
        for (bar_loop_temp = bar_down[1]; bar_loop_temp > L_down_bar_spot[1]; bar_loop_temp--)
        {
          left_edge[bar_loop_temp] = bar_down[0] + bar_k * (ROW_END - bar_loop_temp);
        }
      }
      else    //近处障碍连线
      {
        bar_k = 7.7E-1;       //障碍连线斜率
        for (bar_loop_temp = L_down_bar_spot[1]; bar_loop_temp <= ROW_END; bar_loop_temp++)
        {
          left_edge[bar_loop_temp] = L_down_bar_spot[0] - bar_k * (bar_loop_temp - L_down_bar_spot[1]);
        }
      }
    }
    else if (Bar_flag == 2) //如果为右障碍
    {
      int8 bar_loop_temp = 0;  //障碍循环变量
      uint8 bar_down[2];       //障碍末端下角点
      uint8 bar_up[2];         //障碍末端上角点
      double bar_k;           //障碍斜率
      R_down_bar_spot[0] = right_edge[R_down_bar_spot[1] - 1] - 5;
      bar_up[1] = right_bottom.y;
      bar_up[0] = right_bottom.x;

      /****延长障碍右上角点到图像最上方****/
      bar_k = (R_down_bar_spot[0] - bar_up[0]) / (double)(R_down_bar_spot[1] - bar_up[1]);
      for (bar_loop_temp = bar_up[1]; bar_loop_temp >= 1; bar_loop_temp--)
      {
        right_edge[bar_loop_temp] = bar_up[0] - bar_k * (bar_up[1] - bar_loop_temp) - 5;
      }
      /****障碍中包络外移****/
      for (bar_loop_temp = R_down_bar_spot[1]; bar_loop_temp > bar_up[1]; bar_loop_temp--)
      {
        right_edge[bar_loop_temp] -= 5;
      }
      /****连接障碍右下角点到右边沿最末端位置*****/
      for (bar_loop_temp = ROW_END; bar_loop_temp > 0; bar_loop_temp--)
      {
        if (right_edge[bar_loop_temp] != -1) //如果非空
        {
          bar_down[0] = right_edge[bar_loop_temp];
          bar_down[1] = bar_loop_temp;
          break;
        }
      }
      if (R_down_bar_spot[1] < 40) //如果尚未靠近障碍
      {
        /******连线******/
        bar_k = (bar_down[0] - R_down_bar_spot[0]) / (double)(bar_down[1] - R_down_bar_spot[1]); //计算斜率
        for (bar_loop_temp = bar_down[1]; bar_loop_temp > R_down_bar_spot[1]; bar_loop_temp--)
        {
          right_edge[bar_loop_temp] = bar_down[0] - bar_k * (ROW_END - bar_loop_temp);
        }
      }
      else   //近处障碍连线
      {
        bar_k = 7.7E-1; //计算斜率
        for (bar_loop_temp = R_down_bar_spot[1]; bar_loop_temp <= ROW_END; bar_loop_temp++)
        {
          right_edge[bar_loop_temp] = R_down_bar_spot[0] + bar_k * (bar_loop_temp - R_down_bar_spot[1]);
        }
      }
    }
  }
}

/**************障碍检测函数**************/
void detect_barrier()       //障碍检测函数
{
  if (EXTRACT_ROW >= 25 && (!enable_loop_flag) && (!Bar_flag)) //检测到第30行处
  {
    if (left_edge[EXTRACT_ROW] != -1 && right_edge[EXTRACT_ROW] != -1) //左右边沿均存在时开始检测
    {
      uint8 detect_num = 5;        //障碍检测点的数目
      uint8 detect_interval = 0;   //障碍检测间隔
      uint8 bar_col = 0;           //列循环变量
      uint8 bar_flag = 0;          //障碍标记
      uint8 bar_init_location = 0; //障碍初始位置
      uint8 bar_end_location = 0;  //障碍结束位置
      uint8 bar_pix_width = 0;         //定义障碍像素宽度
      uint8 bar_standard_pix_width = (right_edge[EXTRACT_ROW] - left_edge[EXTRACT_ROW]) / 4; //定义标准障碍宽度为赛道宽度1/4
      uint8 half_road = (right_edge[EXTRACT_ROW] + left_edge[EXTRACT_ROW]) / 2;
      if ((right_edge[EXTRACT_ROW] - left_edge[EXTRACT_ROW]) > 6) //左右边沿之差大于6
      {
        /*****障碍粗判******/
        detect_interval = (right_edge[EXTRACT_ROW] - left_edge[EXTRACT_ROW]) / 5; //粗搜索间隔定为1/5赛道宽度
        for (bar_col = left_edge[EXTRACT_ROW] + 1; bar_col < right_edge[EXTRACT_ROW];)
        {
          if (img_barrel[EXTRACT_ROW][bar_col] <= img_threshold) //如果检测到为黑色
          {
            bar_flag = 1;       //标记为障碍
            break;
          }
          bar_col += detect_interval;
        }
        if (bar_flag == 1) //如果初始判断为障碍，须进行进一步细判
        {
          for (bar_col = left_edge[EXTRACT_ROW] + 1; bar_col < right_edge[EXTRACT_ROW]; bar_col++) //细搜
          {
            if (bar_init_location == 0) //如果未找到障碍初始点
            {
              if ((img_barrel[EXTRACT_ROW][bar_col] > img_threshold) && (img_barrel[EXTRACT_ROW][bar_col + 1] <= img_threshold)) //找到白黑跳变沿，则记录为障碍初始位置
              {
                bar_init_location = bar_col;
              }
            }
            else if (bar_init_location != 0) //如果找到障碍初始点
            {
              if (img_barrel[EXTRACT_ROW][bar_col] <= img_threshold) //如果检测为黑色则计数
              {
                bar_pix_width += 1;
              }
              if (img_barrel[EXTRACT_ROW][bar_col] <= img_threshold && (img_barrel[EXTRACT_ROW][bar_col + 1]) > img_threshold) //如果找到黑白跳变沿，则结束计数
              {
                bar_end_location = bar_col;
                break;
              }
            }
          }
          if (!Bar_flag) //只记录一次障碍下角点
          {
            if (((bar_standard_pix_width - bar_pix_width) <= 5) && ((bar_standard_pix_width - bar_pix_width) >= 0)) //阈值内满足大小
            {
              uint8 middle_bar = (bar_end_location + bar_init_location) / 2;
              if (middle_bar <= half_road)  //如果末端位于左半边，则记录左障碍下角
              {
                Bar_flag = 1; //记录为左障碍
                col_left_edge = bar_end_location; //改变左边沿搜索位置

                /*****记录左下障碍角点*****/
                L_down_bar_spot[0] = bar_end_location;
                L_down_bar_spot[1] = EXTRACT_ROW;
                left_edge[EXTRACT_ROW] = bar_end_location; //重新为左边沿赋值
              }
              else                               //记录右障碍下角
              {
                Bar_flag = 2; //记录为右障碍
                col_right_edge = bar_init_location; //改变右边沿搜索位置

                /*****记录右下障碍角点*****/
                R_down_bar_spot[0] = bar_init_location;
                R_down_bar_spot[1] = EXTRACT_ROW;
                right_edge[EXTRACT_ROW] = bar_init_location; //重新为右边沿赋值
              }
              //          Bee_flag = 1; //蜂鸣器打开
            }
            else if (((bar_pix_width - bar_standard_pix_width) <= 5) && ((bar_pix_width - bar_standard_pix_width) >= 0)) //阈值内满足大小
            {
              if (bar_end_location < half_road)  //如果末端位于左半边，则记录左障碍下角
              {
                Bar_flag = 1; //记录为左障碍
                col_left_edge = bar_end_location; //改变左边沿搜索位置

                /*****记录左下障碍角点*****/
                L_down_bar_spot[0] = bar_end_location;
                L_down_bar_spot[1] = EXTRACT_ROW;
                left_edge[EXTRACT_ROW] = bar_end_location; //重新为左边沿赋值
              }
              else
              {
                Bar_flag = 2; //记录为右障碍
                col_right_edge = bar_init_location; //改变右边沿搜索位置

                /*****记录右下障碍角点*****/
                R_down_bar_spot[0] = bar_init_location;
                R_down_bar_spot[1] = EXTRACT_ROW;
                right_edge[EXTRACT_ROW] = bar_init_location; //重新为右边沿赋值
              }
              //       Bee_flag = 1; //蜂鸣器打开
            }
          }
        }
      }
    }
  }
  if (Bar_flag && (bar_count <= 3)) //之后要进行进一步细判以防误识,继续往后判断3个点
  {
    if (Bar_flag == 1)     //如果是左障碍
    {
      if (EXTRACT_ROW < L_down_bar_spot[1]) //在判断完障碍左下角点后
      {
        if ((left_edge[EXTRACT_ROW] - left_edge[EXTRACT_ROW + 1]) <= 3 && (left_edge[EXTRACT_ROW] - left_edge[EXTRACT_ROW + 1]) >= 0) //向后检测3个点，看是否符合直边边沿特点
        {
          bar_count += 1;   //障碍计数
        }
        else if ((left_edge[EXTRACT_ROW + 1] - left_edge[EXTRACT_ROW]) <= 3 && (left_edge[EXTRACT_ROW + 1] - left_edge[EXTRACT_ROW]) >= 0)
        {
          bar_count += 1;  //障碍计数
        }
        else
        {
          Bar_flag = 0; //寻找错误
          bar_count = 0;
        }
      }
    }
    else if (Bar_flag == 2) //如果是右障碍
    {
      if (EXTRACT_ROW < R_down_bar_spot[1]) //在判断完障碍右下角点后
      {
        if ((right_edge[EXTRACT_ROW + 1] - right_edge[EXTRACT_ROW]) <= 3 && (right_edge[EXTRACT_ROW + 1] - right_edge[EXTRACT_ROW]) >= 0) //向后检测3个点，看是否符合直边边沿特点
        {
          bar_count += 1;    //障碍计数
        }
        else if ((right_edge[EXTRACT_ROW] - right_edge[EXTRACT_ROW + 1]) <= 3 && (right_edge[EXTRACT_ROW] - right_edge[EXTRACT_ROW + 1]) >= 0)
        {
          bar_count += 1;   //障碍计数
        }
        else
        {
          Bar_flag = 0; //说明寻找错误
          bar_count = 0;
        }
      }
    }
  }
}

