/*************弯道“小辫子”去除函数************/
void patch_curve()
{
  /*******局部变量*******/
  uint8 count_curve = 0; //计算消边数

  if ((!enable_loop_flag) && (!Enable_cross_flag)) //如果不是环岛，环岛判断优先
  {
    /*****右弯道消边*****/
    if (right_curve_flag) // 如果是右弯道
    {
      if (L_right_inflection_point + 7 > ROW_END - 1)
      {
        for (EXTRACT_ROW = loop_key_R_Spot[0].y; EXTRACT_ROW >= 0; EXTRACT_ROW--)
        {
          right_edge[EXTRACT_ROW] = -1;
        }
        if (L_lost_count > 50 && R_Init_Continuous_Count > 30) //  //大S型左弯
        {
          right_curve_flag = 0;
          left_curve_flag = 1;
        }
      }
      else
      {
        for (EXTRACT_ROW = L_right_inflection_point + 10; EXTRACT_ROW >= 0; EXTRACT_ROW--)
        {
          right_edge[EXTRACT_ROW] = -1;
        }
        if (L_lost_count > 50 && R_Init_Continuous_Count > 30) //  //大S型左弯
        {
          right_curve_flag = 0;
          left_curve_flag = 1;
        }
      }
    }
    /*****左弯道消边*****/
    else if (left_curve_flag) //如果为左弯道
    {
      if (R_left_inflection_point + 10 > ROW_END - 1)
      {
        for (EXTRACT_ROW = loop_key_L_Spot[0].y; EXTRACT_ROW > 0; EXTRACT_ROW--)
        {
          left_edge[EXTRACT_ROW] = -1;
        }
        if (R_lost_count > 50 && L_Init_Continuous_Count > 30) //  //大S型左弯
        {
          right_curve_flag = 1;
          left_curve_flag = 0;
        }
      }
      else
      {
        for (EXTRACT_ROW = R_left_inflection_point + 10; EXTRACT_ROW >= 0; EXTRACT_ROW--)
        {
          left_edge[EXTRACT_ROW] = -1;
        }
        if (R_lost_count > 50 && L_Init_Continuous_Count > 30) //  //大S型左弯
        {
          right_curve_flag = 1;
          left_curve_flag = 0;
        }
      }

    }
  }
}

/******补线函数出错问题处理函数******/
uint8 Patch_Error_Line(int16 *edge, uint8 current_row)
{
  if (edge[current_row] > COL_END || edge[current_row] < 0)
  {
    //如果所补线超出边界，则沿用上一个边沿
    edge[current_row] = edge[current_row + 1];
    return 1;
  }
  else
    return 0;
}
